select * from (
select rownum rn, A.* from(  
select id, nickname, gnum, gtitle, gcontents, to_char(gdate, 'yyyy-MM-dd') as gdate, gcnt, gimage 
from gongji order by gnum desc)A)
where rn between #{start} and #{end}

